  `no` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '订单编号：按照一定规则编号',
  `customer_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '客户id',
  `company_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '公司id（经销商）',
  `city_area_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '客户所在地区id',
  `address` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '订单客户的地址',
  `acreage` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '客户房屋面积：单位㎡，数据库只存数字',
  `status` tinyint(4) NOT NULL COMMENT '状态：\r\n0 - 待处理（新订单）；\r\n1 - 确认需求；\r\n2 - 设计待审核；\r\n3 - 待设计；\r\n4 - 设计中；\r\n5 - 设计待确认；\r\n6 - 支付待审核；\r\n7 - 待生产；\r\n8 - 生产中：备料；\r\n9 - 生产中：加工；\r\n10 - 生产中：封边；\r\n11 - 生产中：修边；\r\n12 - 生产中：质检；\r\n13 - 生产中：包装；\r\n14 - 生产中：待入库；\r\n15 - 待配送；\r\n16 - 配送中；\r\n17 - 已配送；\r\n18 - 已完成；',
  `salesman` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '业务员（经销商）',
  `merchandiser` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '跟单员（厂方）',
  `creator` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '创建人',
  `created` datetime(0) NOT NULL COMMENT '创建时间',
  `designer` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '设计师',
  `design_scheme` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '参考设计方案',
  `earnest` int(11) NOT NULL COMMENT '客户定金：默认0',
  `imprest` decimal(10, 2) NOT NULL COMMENT '客户预付款：默认0',
  `retainage` decimal(10, 2) NOT NULL COMMENT '客户尾款（保留款）：默认0',
  `amount` decimal(10, 2) NOT NULL COMMENT '客户总金额：经销商给客户的最终报价，其值为 market_price - discounts，默认0',
  `notes` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '描述',
  `design_style` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '设计风格',
  `type` tinyint(4) NOT NULL COMMENT '订单类型：0 - 正常订单；1 - 补产订单；2 - 返货单；3 - 打样订单；4 - 样板订单；5 - 展示厅订单;6 - 补发订单',
  `parent_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '父订单id：当为增补单时，需要写入原订单id',
  `estimated_delivery_date` date NULL DEFAULT NULL COMMENT '预计交货日期：根据厂家的算法，在确认生产时计算一个日期保存进去',
  `delivery_date` date NULL DEFAULT NULL COMMENT '实际交货日期',
  `design_fee` int(11) NOT NULL COMMENT '设计费：默认0',
  `factory_price` decimal(10, 2) NOT NULL COMMENT '厂家报价：对经销商的报价，客户看不到，默认0，即出厂价',
  `customer_tel` char(11) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '客户电话',
  `market_price` decimal(10, 2) NOT NULL COMMENT '市场价：按市场单价计算出来的价格',
  `discounts` decimal(10, 2) NOT NULL COMMENT '优惠/折扣额：对客户优惠多少钱',
  `factory_discounts` decimal(10, 2) NOT NULL COMMENT '工厂优惠价：工厂对经销商的优惠/折扣额，默认0',
  `factory_final_price` decimal(10, 2) NOT NULL COMMENT '工厂的最终报价：值为 factory_price - factory_discounts，默认为0',
  `is_confirm_fprice` tinyint(1) NOT NULL COMMENT '厂方最终报价是否已确认：false - 未确认；true - 已确认；已确认的，订单报价不可更改，默认为false',
  `is_confirm_cprice` tinyint(1) NOT NULL COMMENT '经销商最终报价是否已确认：false - 未确认；true - 已确认；已确认的，订单报价不可更改，默认为false',
  `is_design` tinyint(1) NULL DEFAULT NULL COMMENT '是否设计  0 - 不需要设计   1 - 需要设计',
  `time_consuming` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '耗时',
  `is_coordination` tinyint(1) NULL DEFAULT NULL COMMENT '是否需要外协: 0 - 不需要 ; 1 - 需要',
  `documentary_notes` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '跟单备注',
