-- ----------------------------
-- Table structure for custom_order_files
-- ----------------------------
DROP TABLE IF EXISTS `custom_order_files`;
CREATE TABLE `custom_order_files`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `path` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的访问路径：相对路径',
  `full_path` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '全路径：加上服务域名的路径，富文本中的图片引用该路径',
  `mime` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的mime类型',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的名称',
  `custom_order_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '定制订单的id',
