  INDEX `IXFK_custom_order_files_user_id`(`creator`) USING BTREE,
  INDEX `IXFK_custom_order_files_custom_order_id`(`custom_order_id`) USING BTREE,
  CONSTRAINT `FK_custom_order_files_custom_order_id` FOREIGN KEY (`custom_order_id`) REFERENCES `custom_order` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_custom_order_files_user_id` FOREIGN KEY (`creator`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '预约设计图存储表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for custom_order_log
