  CONSTRAINT `FK_custom_order_log_user_creator` FOREIGN KEY (`creator`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '定制订单操作日志：在订单详情里展示操作日志，也可在订单列表里通过按钮打开操作日志' ROW_FORMAT = Dynamic;

-- ----------------------------
