-- ----------------------------
-- Table structure for dealer_account_details
-- ----------------------------
DROP TABLE IF EXISTS `dealer_account_details`;
CREATE TABLE `dealer_account_details`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '明细名称：比如2018双十一活动保证金',
  `dealer_account_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `payment_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '非支付记录引起的账号明细，不用填',
  `funds` tinyint(4) NOT NULL COMMENT '资金款项：0 - 自由资金；1 - 定向预付金；2 - 定时预付金；3 - 保证金；4 - 加盟费；6 - 设计保证金；7 - 活动诚意金；8 - 合作诚意金，其他类型根据需求再添加',
  `type` tinyint(4) NOT NULL COMMENT '类型：0 - 无限制（常指自由资金）；1 - 定向可用（只有购买指定东西时可用）；2 - 定时可用（在指定时间可用）；3 - 固定期间可用（常指优惠活动）；4 - 冻结期间不可用',
  `start_time` datetime(0) NULL DEFAULT NULL COMMENT '开始时间：与type配合使用，不需要设置的不保存任何数据',
  `end_time` datetime(0) NULL DEFAULT NULL COMMENT '截至时间：与type结合使用，指设置固定期限的结束时间，不需要时可不填',
