  `creator` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '创建该明细的操作人',
  `created` datetime(0) NOT NULL COMMENT '创建的日期',
  `used_time` datetime(0) NULL DEFAULT NULL COMMENT '被使用的日期：抵扣货款或转入自由账号或提现的时间',
  `used_user` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '抵扣货款或转入自由账号或提现的操作人',
  `notes` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '说明：描述信息',
  `amount` decimal(10, 2) NOT NULL COMMENT '金额：使用时从相应账号扣除',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_dealer_account_details_dealer_account_id`(`dealer_account_id`) USING BTREE,
  INDEX `IXFK_dealer_account_details_payment`(`payment_id`) USING BTREE,
  INDEX `IXFK_dealer_account_details_user_creator`(`creator`) USING BTREE,
  INDEX `IXFK_dealer_account_details_user_used_user`(`used_user`) USING BTREE,
  CONSTRAINT `FK_dealer_account_details_dealer_account_id` FOREIGN KEY (`dealer_account_id`) REFERENCES `dealer_account` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_dealer_account_details_payment` FOREIGN KEY (`payment_id`) REFERENCES `payment` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_dealer_account_details_user_creator` FOREIGN KEY (`creator`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_dealer_account_details_user_used_user` FOREIGN KEY (`used_user`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '财务账户的资金明细：记录每一条款项的用途以及是否可用，是否已使用' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for dealer_account_log
-- ----------------------------
DROP TABLE IF EXISTS `dealer_account_log`;
CREATE TABLE `dealer_account_log`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `dealer_account_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '经销商财务账号',
  `content` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '交易内容：比如 订单扣款：订单编号为xxx，订单客户为xxx，客户电话为xxx，一般情况下由系统生成',
  `type` tinyint(4) NOT NULL COMMENT '交易类型：0 - 线下充值；1 - 线上充值；2 - 订单扣款；3 - 设计扣款；4 - 提现；5 - 转入；6 - 转出；',
