  INDEX `IXFK_dealer_account_log_user_creator`(`creator`) USING BTREE,
  INDEX `company_id`(`company_id`) USING BTREE,
  CONSTRAINT `FK_dealer_account_log_dealer_account_id` FOREIGN KEY (`dealer_account_id`) REFERENCES `dealer_account` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_dealer_account_log_user_creator` FOREIGN KEY (`creator`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `dealer_account_log_ibfk_1` FOREIGN KEY (`company_id`) REFERENCES `company` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '经销商账号操作日志：查询排序按id或created倒序,记录的是账户金额的出入日志，只有账户余额发生变化时才产生日志' ROW_FORMAT = Dynamic;

