-- ----------------------------
-- Table structure for payment
-- ----------------------------
DROP TABLE IF EXISTS `payment`;
CREATE TABLE `payment`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `type` tinyint(4) NOT NULL COMMENT '支付类型：0 - 客户对经销商；1 - 经销商对客户（常指退款）；2 - 经销商对厂家；3 - 厂家对经销商（常指退款）;4 - 厂家对经销商扣款,;5 - 厂家外协支付',
  `way` tinyint(4) NOT NULL COMMENT '支付方式：0 - 支付宝；1 - 微信；2 - 现金；3 - 银行转账（银联或POS机）；',
  `funds` tinyint(4) NOT NULL COMMENT '支付款项：0 - 定金；1 - 预付款；2 - 尾款；3 - 全款；4 - 设计金；5 - 自由资金；6 - 定向预付金；7 - 定时预付金；8 - 保证金；9 - 加盟费；10 - 设计保证金；11 - 活动诚意金；12 - 合作诚意金，13- 货款;14 - 外协;20 - 其他，其中4是厂家对经销商订单单独收取的设计费用，不计入任何账号，从5开始需要计入相应的账号，并生成dealer_account_detail数据，0 - 3是客户付给经销商的款项，计入经销商个人账号，不用生成dealer_account_detail数据\r\n',
  `notes` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '支付说明',
  `creator` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '创建人',
  `created` datetime(0) NOT NULL COMMENT '创建时间',
  `status` tinyint(4) NOT NULL COMMENT '付款状态：0 - 待付款；1 - 已付款；默认0，审核后改为1',
  `custom_order_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '订单id：当为订单的支付时，保存订单id',
  `auditor` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '审核人',
  `audited` datetime(0) NULL DEFAULT NULL COMMENT '审核时间',
  `amount` decimal(10, 2) NOT NULL COMMENT '支付金额：默认0',
  `payee` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '付给经销商的，设为经销商的店主账号；付给厂家的，设为厂家的系统账号（系统内置的公司账号）',
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '标题',
  `company_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '公司id，保存经销商的公司id',
  `holder` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '收款人',
  `holder_account` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '收款账号',
  `deposit_bank` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '开户行',
  `pay_time` datetime(0) NULL DEFAULT NULL COMMENT '支付时间',
  `no` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '支付编号',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_payment_user_auditor`(`auditor`) USING BTREE,
  INDEX `IXFK_payment_user_creator`(`creator`) USING BTREE,
  INDEX `IXFK_payment_user_payee`(`payee`) USING BTREE,
  INDEX `FK_payment_company_company_id`(`company_id`) USING BTREE,
  INDEX `IXFK_payment_custom_order__id`(`custom_order_id`) USING BTREE,
  CONSTRAINT `FK_payment_company_company_id` FOREIGN KEY (`company_id`) REFERENCES `company` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_payment_custom_order_id` FOREIGN KEY (`custom_order_id`) REFERENCES `custom_order` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_payment_user_auditor` FOREIGN KEY (`auditor`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_payment_user_creator` FOREIGN KEY (`creator`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `FK_payment_user_payee` FOREIGN KEY (`payee`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '支付记录：1. 经销商打给厂家的，审核通过后，累加到经销商在厂家端的自由账号上，并生成账户日志；2 - 客户打给经销商的，审核通过后累加到经销商自己的私有账户上，并生成账户日志；' ROW_FORMAT = Dynamic;

