  `sys_type` tinyint(4) NULL DEFAULT NULL COMMENT '系统类型，0：工业4.0整合版，1：工业4.0F端，2：工业4.0B端，3：工业4.0C端',
  `platform` tinyint(4) NULL DEFAULT NULL COMMENT '平台,0:ios,1:android，2：PC端',
  `url` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '下载地址',
  `md5_value` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'MD5校验码',
  `update_state` tinyint(4) NULL DEFAULT NULL COMMENT '是否有效，0：无效，1：有效，默认：0',
  `file_size` int(11) NULL DEFAULT NULL COMMENT '文件大小',
  `description` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '更新说明',
  `create_user` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建人',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `update_user` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '修改人',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`version_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for upload_files
-- ----------------------------
DROP TABLE IF EXISTS `upload_files`;
CREATE TABLE `upload_files`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '主键',
  `company_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '公司id',
  `resource_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '所属资源id，比如商品、商品评论、',
  `resource_type` tinyint(4) NOT NULL COMMENT '文件的所属资源类型：0-系统配置;1-商城配置;2-商品;3-商品评论;4-快享;5-商品规格;6-品牌;7-用户相关（头像、桌面背景等）8-背景图片,9-商品展示图片;',
  `path` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的访问路径，带域名的全路径，如：http://localhost:8080/resources/goods/3232233323.png',
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '文件的名称：被上传时的原始文件名',
  `mime` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的mime类型（上传时根据业务逻辑而设定的）',
  `original_mime` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件的mime类型（原始mime）',
  `status` tinyint(4) NOT NULL COMMENT '文件状态：0 - 临时文件；1：正式文件',
  `creator` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '创建人id（上传人id）',
  `created` datetime(0) NOT NULL COMMENT '上传时间',
  `belong_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '该文件对应的资源属于哪个资源的子资源：比如商品评论的图片，那么belong_id填对应的商品id，没有则为null',
  `full_path` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件访问全路径：带域名的路径',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_upload_files_company_id`(`company_id`) USING BTREE,
  CONSTRAINT `FK_upload_files_company_id` FOREIGN KEY (`company_id`) REFERENCES `company` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '上传的文件管理表，用于文件维护，临时文件可以被清理' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '名称，用于显示',
  `sex` tinyint(4) NOT NULL COMMENT '性别:0-男，1-女',
  `email` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL,
  `avatar` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '头像，默认为AppBeanInjector.configuration.getUserDefaultAvatar()',
  `birthday` date NULL DEFAULT NULL COMMENT '生日',
  `mobile` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL,
  `state` tinyint(4) NOT NULL COMMENT '状态：0-禁用，1-启用，默认为1',
  `background` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '自定义背景图',
  `created` datetime(0) NOT NULL COMMENT '创建时间',
  `last_login` datetime(0) NULL DEFAULT NULL COMMENT '最后登陆时间',
  `time_zone` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '时区',
  `language` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '语言',
  `city_area_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '所处城市id，精确到区（可用于匹配附近门店）',
  `followers` int(11) NOT NULL COMMENT '粉丝数量：默认为0，没添加一个关注人，该字段增加1，相反则减一，减到0为止',
  `login_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '登录名(默认为手机号)，用户只能修改一次',
  `type` tinyint(4) NOT NULL COMMENT '账号类型：0 - 厂家（factory）；1 - 经销商（dealer）；2 - 客户（client）；3 - 共享设计师（或安装工、家居顾问）用户（share）;4 - 系统(system或者admin);5 - 超级管理员(super_admin);',
  `is_changed_login_name` tinyint(1) NOT NULL COMMENT '登录名是否已被更改过，false-未被更改；true-已更改（一个用户只能修改一次登录名）',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `IXUK_login_name`(`login_name`) USING BTREE,
  UNIQUE INDEX `UK_mobile`(`mobile`) USING BTREE,
  UNIQUE INDEX `UK_email`(`email`) USING BTREE,
  INDEX `IXFK_user_city_area_id`(`city_area_id`) USING BTREE,
  CONSTRAINT `FK_user_city_area_id` FOREIGN KEY (`city_area_id`) REFERENCES `city_area` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '客户用户表（会员）' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_attention
-- ----------------------------
DROP TABLE IF EXISTS `user_attention`;
CREATE TABLE `user_attention`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '关注人id',
  `resource_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '被关注的资源id：当关注的是店铺时，保存的是公司id',
  `resource_type` tinyint(4) NOT NULL COMMENT '被关注的资源类型：0 - 关注的是店铺；1 - 关注的是人',
  `created` datetime(0) NOT NULL COMMENT '关注时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `IXUNIQUE_user_attention`(`user_id`, `resource_id`, `resource_type`) USING BTREE,
  INDEX `IXFK_user_attention_user_user_id`(`user_id`) USING BTREE,
  CONSTRAINT `FK_user_attention_user_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '用户的关注资源' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_basis
-- ----------------------------
DROP TABLE IF EXISTS `user_basis`;
CREATE TABLE `user_basis`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '主键ID',
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '用户主键ID',
  `political_outlook` tinyint(4) NULL DEFAULT NULL COMMENT '政治面貌  0 Communist_Party_members(中共党员),1 Communist Youth League member(共青团员),2 Masses(群众)',
  `address` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '家庭地址',
  `work_unit` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '工作单位',
  `work` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '职位',
  `income` tinyint(4) NULL DEFAULT NULL COMMENT '收入 0 one(2000~4000),1 two(4000~6000),2 three(6000~8000),3 four(8000~10000)',
  `education` tinyint(4) NULL DEFAULT NULL COMMENT '学历 0-master(硕士),1-Undergraduate(本科),2-Junior_College(大专),3-(high_school)高中,4-school(中专),5-Junior_middle_school(初中)',
  `nation` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '民族',
  `identity_number` varchar(18) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '身份证号',
  `qq_number` varchar(15) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT 'QQ号',
  `wechat_number` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '微信号',
  `contact_number` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '联系电话',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_user_basis_user`(`user_id`) USING BTREE,
  CONSTRAINT `FK_user_basis_user` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '用户基础信息表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_extra
-- ----------------------------
DROP TABLE IF EXISTS `user_extra`;
CREATE TABLE `user_extra`  (
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `salt` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `password` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `updated` datetime(0) NULL DEFAULT NULL,
  `token` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  PRIMARY KEY (`user_id`) USING BTREE,
  INDEX `IXFK_user_extra_user_id`(`user_id`) USING BTREE,
  CONSTRAINT `FK_user_extra_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '用户扩展信息表，存储密码等隐私信息' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_notify
-- ----------------------------
DROP TABLE IF EXISTS `user_notify`;
CREATE TABLE `user_notify`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `company_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '公司id',
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '消息接收人',
  `grouping` tinyint(4) NULL DEFAULT NULL COMMENT '消息分组：来源于NotifyGrouping.java，暂时不用，默认为空',
  `is_readed` bit(1) NOT NULL COMMENT '是否已读',
  `created` datetime(0) NOT NULL COMMENT '创建时间',
  `top_time` datetime(0) NOT NULL COMMENT '消息置顶的时间：默认为创建时间，取消置顶时设为创建时间，查询列表时按置顶时间倒序',
  `content` varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '消息详情',
  `link_path` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '链接路径：当不为空时，点击链接可以跳到相应的资源',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '消息标题，用于消息列表显示',
  `type` tinyint(4) NOT NULL COMMENT '消息类型：0 - 系统消息；1 - 个人消息',
  `sender` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '消息发送人：系统消息，设置为系统账号',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_user_notify_company_id`(`company_id`) USING BTREE,
  INDEX `IXFK_user_notify_user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_schedule
-- ----------------------------
DROP TABLE IF EXISTS `user_schedule`;
CREATE TABLE `user_schedule`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '主键Id',
  `time` datetime(0) NOT NULL COMMENT '日程日期',
  `size` int(11) NULL DEFAULT NULL COMMENT '事件个数',
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '用户Id',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_user_schedule_user`(`user_id`) USING BTREE,
  CONSTRAINT `FK_user_schedule_user` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '日程日期表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_schedule_item
-- ----------------------------
DROP TABLE IF EXISTS `user_schedule_item`;
CREATE TABLE `user_schedule_item`  (
  `id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '主键id',
  `type` tinyint(4) NOT NULL COMMENT '0 个人 1 系统',
  `notes` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '描述 不可为空',
  `start_time` datetime(0) NOT NULL COMMENT '计划中的开始时间',
  `end_time` datetime(0) NOT NULL COMMENT '计划中的结束时间',
  `user_schedule_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `status` tinyint(4) NOT NULL COMMENT '日程状态：0待完成 1已完成 2延期 3终止',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `IXFK_user_schedule_item_user_schedule`(`user_schedule_id`) USING BTREE,
  CONSTRAINT `FK_user_schedule_item_user_schedule` FOREIGN KEY (`user_schedule_id`) REFERENCES `user_schedule` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '用户日程明细表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for user_third_info
-- ----------------------------
DROP TABLE IF EXISTS `user_third_info`;
CREATE TABLE `user_third_info`  (
  `user_id` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '用户id，关联user表的id',
  `wx_open_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT 'openid 微信公众号的用户标识，对当前开发者帐号唯一。一个openid对应一个公众号',
  `wx_nickname` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '微信昵称',
  `wx_union_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT 'unionid 微信开放平台的用户统一标识。针对一个微信开放平台帐号下的应用，同一用户的unionid是唯一的，默认设为open_id',
  `wx_is_subscribe` bit(1) NOT NULL COMMENT '是否订阅公众号，用户关注公众号时生成用户数据，该字段为1（true），当用户取消关注时，设为0（false）',
  `wx_is_bind` bit(1) NOT NULL COMMENT '微信是否绑定:0-否,1-是，默认true',
  `email_is_bind` bit(1) NOT NULL COMMENT '信箱是否绑定:0-否,1-是，不填信箱时，为false',
  `mobile_is_bind` bit(1) NOT NULL COMMENT '手机号是否绑定:0-否,1-是，不填手机号时，默认为false',
  `rongcloud_token` varchar(260) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '融云平台的token值',
  `app_token` varchar(260) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT 'app的token值',
  PRIMARY KEY (`user_id`) USING BTREE,
  INDEX `IXFK_user_third_info_user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin COMMENT = '第三方账号信息表，微信、钉钉等' ROW_FORMAT = Dynamic;

