SELECT

FROM aftersale_apply
WHERE id = #{id}

SELECT aa.*,co.orderNo,co.customerName,co.creatorName
from aftersale_apply aa
LEFT JOIN (SELECT co.id orderId,co.no orderNo,u.name customerName,us.name creatorName
from custom_order co
LEFT JOIN user u on co.customer_id=u.id
left join user us on us.id = co.creator
) co on aa.custom_order_id=co.orderId




            (co.customerName like '%${condition}%'
            or co.orderNo like '%${condition}%'
            or aa.no like '%${condition}%')


            AND aa.custom_order_id = #{customOrderId}


            AND aa.type = #{type}


            AND aa.notes = #{notes}


            AND aa.company_id = #{companyId}


            AND aa.status = #{status}


            AND aa.checker = #{checker}


            AND aa.check_time = #{checkTime}


            AND aa.result = #{result}


            AND aa.creator = #{creator}


            AND aa.created = #{created}


            AND aa.branch_id = #{branchId}


            AND aa.no like '%${no}%'


            AND aa.information = #{information}


            AND aa.result_order_id = #{resultOrderId}

select aa.*,c.name as company_name,o.no as orderNo
from aftersale_apply aa
left join company c on c.id = aa.company_id
left join custom_order o on aa.custom_order_id = o.id



            AND aa.custom_order_id = #{customOrderId}


            AND c.name like '%${companyName}%'


            AND aa.customer_name like '%${customerName}%'


            AND o.no like '%${orderNo}%'


            AND aa.no = #{no}


            AND aa.custom_order_id = #{customOrderId}


            AND aa.type = #{type}


            AND aa.notes = #{notes}


            AND aa.company_id = #{companyId}


            AND aa.status in

                #{sta}



            AND aa.checker = #{checker}


            AND aa.check_time = #{checkTime}


            AND aa.result = #{result}


            AND aa.creator = #{creator}


            AND aa.branch_id = #{branchId}


            AND aa.type = #{type}


            AND aa.result_order_id = #{resultOrderId}


               and DATE_FORMAT(aa.created, '%Y-%m-%d')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d')   


              and DATE_FORMAT(aa.created, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')

select
aa.order_product_id aftersaleProductid,aa.id, aa.custom_order_id customOrderId, aa.type, aa.notes, aa.company_id companyId,
aa.status, aa.checker, aa.check_time checkTime,aa.result, aa.creator, aa.created, aa.no,aa.information,aa.reason,
aa.result_order_id resultOrderId,aa.is_charge charge,aa.charge_amount chargeAmount,aa.pay_amount payAmount,aa.payment_id paymentId, aa.from_type fromType,
c.no orderNo,c.customer_id customerId,c.address customerAddress,c.consignee_tel consigneeTel,u.name checkerName,
uuu.name customerName,u.mobile customerMobile,uu.name creatorName, cp.name companyName, cp.leader_tel companyTel
from aftersale_apply aa left join custom_order c on aa.custom_order_id = c.id
left join user u on aa.checker = u.id
left join user uuu on c.customer_id = uuu.id
left join user uu on uu.id = c.creator
left join company cp on cp.id = c.company_id



            aa.id = #{id}


            AND aa.custom_order_id = #{customOrderId}


            AND aa.type = #{type}


            AND aa.notes = #{notes}


            AND aa.company_id = #{companyId}


            AND aa.status = #{status}


            AND aa.checker = #{checker}


            AND aa.check_time = #{checkTime}


            AND aa.result = #{result}


            AND aa.creator = #{creator}


            AND aa.created = #{created}


            AND aa.no like CONCAT('%',#{no},'%')


            AND aa.branch_id = #{branchId}


            AND aa.result_order_id = #{resultOrderId}


            AND c.no = #{orderNo}

select count(*) from aftersale_apply



            company_id = #{companyId}


            AND custom_order_id in (select id from custom_order where salesman=#{saleMan})


            AND type = #{type}


            AND date_format(created,'%Y-%m')=#{startTime}


            AND created < #{endTime}


            AND result like '%${result}%'

select * from aftersale_apply



            company_id = #{companyId}


            AND custom_order_id in (select id from custom_order where salesman=#{saleMan})


            AND date_format(created,'%Y-%m')=#{startTime}


    and result_order_id is not null

select count(*) from aftersale_apply



            company_id = #{companyId}


            AND custom_order_id in (select id from custom_order where salesman=#{saleMan})


            AND status = #{status}


            AND date_format(created,'%Y-%m')=#{startTime}

select count(*) from custom_order where type=#{orderType}
and id in

    #{id}

select date_format(created,'%Y-%m-%d') creatTime,IFNULL(COUNT(*) ,0) as count from aftersale_apply



            company_id = #{companyId}


            AND custom_order_id in (select id from custom_order where salesman=#{saleMan})


            AND type = #{type}


            AND date_format(created,'%Y-%m')=#{startTime}


            AND created < #{endTime}


            AND result like '%${result}%'



GROUP BY date_format(created,'%Y-%m-%d') order by created

select count(*) from aftersale_apply



            company_id = #{companyId}


            AND status = #{status}

        and DATE_FORMAT( created, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )

select aa.custom_order_id customOrderId,aa.result result,co.factory_final_price factoryFinalPrice,(SELECT count(*) FROM order_product WHERE custom_order_id=aa.custom_order_id) orderProductCount,aa.type,aa.status,aa.id aftersaleId,co.id orderId,aa.no orderNo,cc.name customerName,cc.phone customerPhone, cp.name companyName,op.type productType,op.series productSeries, aa.created created,co.documentary_time documentaryTime, cor.no customOrderNO from ( select
from aftersale_apply
date_format(created,'%Y-%m-%d') >= #{startTime}
id = #{id}
AND date_format(created,'%Y-%m-%d') < #{endTime}
AND company_id in (select id from company where leader in (select id from user where name like '%${leaderName}%'))
AND custom_order_id in (select id from custom_order where customer_id in (select id from user where name like '%${customerName}%'))
AND result_order_id in (select id from custom_order where city_area_id in (select id from city_area where merger_name like '%${mergerName}%'))
AND result_order_id in (select custom_order_id from order_product where type=#{productType})
AND result_order_id in (select custom_order_id from order_product where series=#{productSeries})
AND no like CONCAT('%',#{orderNo},'%')
AND date_format(created,'%Y-%m-%d')=#{created}
AND result_order_id in (select id from custom_order where date_format(documentary_time,'%Y-%m-%d')=#{produceTime})
AND result_order_id in (select order_id from dispatch_bill where date_format(actual_date,'%Y-%m-%d')=#{actualDate})
AND status = #{status}
AND to_days(created) = to_days(now())
AND YEARWEEK(date_format(created,'%Y-%m-%d')) = YEARWEEK(now())
AND DATE_FORMAT( created, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
AND status in (#{status})
AND branch_id = #{branchId}
AND company_id = #{companyId}
AND type = #{type}
)aa left join company cp on aa.company_id=cp.id left join custom_order co on aa.result_order_id=co.id left join custom_order cor on aa.custom_order_id=cor.id left join company_customer cc on cor.customer_id=cc.id left join order_product op on op.custom_order_id=aa.result_order_id order by created desc

select aa.type,aa.status,aa.id aftersaleId,co.id orderId,aa.no aftersaleNo,co.no orderNo,cc.name
customerName,cp.name companyName,op.type productType,op.series productSeries,
aa.created created,co.documentary_time documentaryTime
from aftersale_apply aa
left join company cp on aa.company_id=cp.id
left join custom_order co on aa.custom_order_id=co.id
left join company_customer cc on co.customer_id=cc.id
left join order_product op on op.after_apply_id=aa.id



            aa.branch_id=#{branchId}


            AND (aa.company_id in (select id from company where leader_tel like '%${condation}%')
            or cc.name like '%${condation}%'
            or co.no like '%${condation}%')


            and co.city_area_id=#{cityId}


            and co.company_id=#{dealerId}


            and aa.no=#{no}


            and co.salesman=#{salesman}


            AND date_format(co.created,'%Y-%m-%d') >=  #{startTime}


            AND date_format(co.created,'%Y-%m-%d')  <=  #{endTime}



order by created desc

select o.*, us.name as customerName, substring(c.merger_name,4) as cityName,u.name as salesmanName,u.mobile as salesmanTel,u.avatar as salesmanAvatar,uses.name designerName,uu.name creatorName,uuu.name placeOrderName,uuuu.name receiverName,op.no aftersaleProductNo,bs.value seriesName, op.pay_time payTime,op.door_color,op.start_produce_time startProduceTime,b.value orderStatus,bb.value typeName,bbb.value designName ,c2.name dealerName,c2.leader_tel dealerTel,c2.city_area_id dealerCityAreaId,c2.address dealerAddress FROM order_product op left join basecode bs on bs.code=op.series and bs.type='orderProductSeries' left join custom_order o on op.custom_order_id=o.id left join company c2 on o.company_id = c2.id left join basecode b on b.code = o.status and b.type = 'orderStatus' left join basecode bb on bb.code = o.type and bb.type = 'orderType' left join basecode bbb on bbb.code = o.is_design and bbb.type = 'orderDesign' left join city_area c on o.city_area_id = c.id left join user u on u.id = o.salesman left join company_customer us on us.id = o.customer_id left join user uses on uses.id = o.designer left join user uu on uu.id = o.creator left join user uuu on uuu.id=o.place_order left join user uuuu on uuuu.id=o.receiver
and o.state=1 and o.type=1
and (o.no like concat('%',#{no},'%') or o.old_no like concat('%',#{no},'%') or op.no like concat('%',#{no},'%'))
AND o.customer_tel like concat('%',#{customerTel},'%')
AND o.parent_id =#{orderId}
AND o.status in
#{statu}
AND o.status != 8
AND o.type = #{type}
AND o.company_id = #{companyId}
AND us.name like CONCAT('%',#{customerName},'%')
AND o.designer is null
AND o.designer is not null
AND o.designer = #{designer}
AND c2.leader_tel like CONCAT('%',#{dealerTel},'%')
AND c2.address like CONCAT('%',#{dealerAddress},'%')
AND o.address like CONCAT('%',#{address},'%')
AND c2.name like CONCAT('%',#{companyName},'%')
and o.customer_tel like CONCAT('%',#{customerTel},'%')
and DATE_FORMAT(o.created,'%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(o.created,'%Y-%m-%d') <= #{endTime}
and o.branch_id = #{branchId}
and o.is_design = #{design}
and o.place_order in (select id from user where name like '%${placeOrder}%')
and o.receiver in (select id from user where name like '%${receiver}%')
and o.receiver =#{loginId} or o.place_order=#{loginId}
order by type desc,created desc
