and o.state=#{state}
AND c2.leader_tel like CONCAT('%',#{dealerTel},'%')
AND c2.address like CONCAT('%',#{dealerAddress},'%')
AND o.address like CONCAT('%',#{address},'%')
AND c2.name like CONCAT('%',#{companyName},'%')
and o.customer_tel like CONCAT('%',#{customerTel},'%')
