) o left join city_area c on o.city_area_id = c.id left join user u on u.id = o.salesman left join company_customer us on us.id = o.customer_id left join user uses on uses.id = o.designer left join user uu on uu.id = o.creator left join user uuu on uuu.id=o.place_order left join user uuuu on uuuu.id=o.receiver left join payment p on p.custom_order_id=o.id and funds=31 left join user ur on ur.id=p.auditor
(o.no like upper(concat('%',#{no},'%')) or o.old_no like upper(concat('%',#{no},'%')))
