select o.*, us.name as customerName, substring(c.merger_name,4) as cityName,u.name as salesmanName,u.mobile as salesmanTel,u.avatar as salesmanAvatar,uses.name designerName,uu.name creatorName,uuu.name placeOrderName FROM (select distinct o.*,b.value orderStatus,bb.value typeName,bbb.value designName ,c2.name dealerName,c2.leader_tel dealerTel,c2.city_area_id dealerCityAreaId,c2.address dealerAddress ,p.pay_time payTime from custom_order o left join company c2 on o.company_id = c2.id left join basecode b on b.code = o.status and b.type = 'orderStatus' left join basecode bb on bb.code = o.type and bb.type = 'orderType' left join basecode bbb on bbb.code = o.is_design and bbb.type = 'orderDesign' left join payment p on o.id = p.custom_order_id
and (p.funds = #{funds} or p.funds is null) and p.status=1 and DATE_FORMAT(p.created,'%Y-%m') != DATE_FORMAT(p.audited,'%Y-%m')
and o.branch_id = #{branchId}
and o.is_change = #{change}
and o.no like '%${no}%' or o.old_no like '%${no}%'
and c2.name like '%${name}%'
and c2.address like '%${address}%'
) o left join city_area c on o.city_area_id = c.id left join user u on u.id = o.salesman left join company_customer us on us.id = o.customer_id left join user uses on uses.id = o.designer left join user uu on uu.id = o.creator left join user uuu on uuu.id=o.place_order order by o.is_change asc,o.created desc

