select o.*,c2.name dealerName,uuu.name receiverName,uuuu.name placeOrderName,b.value orderStatus from custom_order o left join company c2 on o.company_id = c2.id left join user u on o.salesman=u.id left join user uu on o.creator=uu.id left join user uuu on o.receiver=uuu.id left join user uuuu on o.place_order=uuuu.id left join basecode b on b.code=o.status and b.type='orderStatus'
o.branch_id=#{branchId} and o.type=#{type}
and o.branch_id = #{branchId}
and o.no like '%${orderNo}%' or o.old_no like '%${orderNo}%'
and u.name like '%${saleName}%' or uu.name like '%${saleName}%' or uuu.name like '%${saleName}%' or uuuu.name like '%${saleName}%'
and u.id like '%${saleId}%' or uu.id like '%${saleId}%' or uuu.id like '%${saleId}%' or uuuu.id like '%${saleId}%'
and c2.name like '%${dealerName}%'
and o.status =#{status}
AND date_format(o.created,'%Y-%m-%d') >= #{startTime}
AND date_format(o.created,'%Y-%m-%d') <= #{endTime}
AND to_days(o.created) = TO_DAYS(now())
AND week(o.created) = week(now())
AND month(o.created) = month(now())
AND QUARTER(o.created) = QUARTER(now())
AND year(o.created) = year(now())

