select a.* from (select count(op.id) count,bs.value name from order_product op left join basecode bs on bs.code=op.type and bs.type='orderProductType' left join custom_order co on op.custom_order_id=co.id where co.branch_id=#{branchId} and co.status in (1,2,3,4,5,6,7) and DATE_FORMAT(co.commit_time,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(co.commit_time,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d') and op.type is not null
AND co.type=#{orderType}
GROUP BY op.type)a order by a.count desc limit 1

select a.* from (select count(op.id) count,bs.value name from order_product op left join basecode bs on bs.code=op.series and bs.type='orderProductSeries' left join custom_order co on op.custom_order_id=co.id where co.branch_id=#{branchId} and co.status in (1,2,3,4,5,6,7) and DATE_FORMAT(co.commit_time,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(co.commit_time,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d') and op.series is not null
AND co.type=#{orderType}
AND op.type=#{type}
GROUP BY op.series)a order by a.count desc limit 1

select a.* from (select count(op.id) count,op.door name from order_product op left join custom_order co on op.custom_order_id=co.id where co.branch_id=#{branchId} and co.status in (1,2,3,4,5,6,7) and DATE_FORMAT(co.commit_time,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(co.commit_time,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d') and op.door is not null and op.door != ''
AND co.type=#{orderType}
AND op.type=#{type}
GROUP BY op.door)a order by a.count desc limit 1

select a.* from (select count(op.id) count,op.door_color name from order_product op left join custom_order co on op.custom_order_id=co.id where co.branch_id=#{branchId} and co.status in (1,2,3,4,5,6,7) and DATE_FORMAT(co.commit_time,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(co.commit_time,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d') and op.door_color is not null and op.door_color != ''
AND co.type=#{orderType}
AND op.type=#{type}
GROUP BY op.door_color)a order by a.count desc limit 1

select a.* from (select count(co.id) count ,c.name as name from custom_order co left join company c on co.company_id=c.id where co.branch_id=#{branchId} and co.status != 8 and DATE_FORMAT(co.commit_time,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(co.commit_time,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND co.type=#{orderType}
group by co.company_id )a order by a.count desc limit 1

