        and  status != 8

            AND branch_id = #{branchId}


            and  DATE_FORMAT(created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')


            and  DATE_FORMAT(created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')


            AND is_coordination=#{coordination}


            AND type=#{orderType}

select count(id) from custom_order
and status>1 and status <= 7
AND branch_id = #{branchId}
and DATE_FORMAT(created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND is_coordination=#{coordination}

select IFNULL(sum(factory_final_price),0) auditMoney from custom_order
and status>1 and status <= 7
AND branch_id = #{branchId}
and DATE_FORMAT(created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND is_coordination=#{coordination}

select count(id) from custom_order
and ( status<2 or status=9 )
AND branch_id = #{branchId}
and DATE_FORMAT(created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND is_coordination=#{coordination}

select IFNULL(sum(factory_final_price),0) noAuditMoney from custom_order
and (status<2 or status=9)
AND branch_id = #{branchId}
and DATE_FORMAT(created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')
AND is_coordination=#{coordination}

select  count(op.id) count,IFNULL(SUM(op.price),0 )productPrice
       from order_product op
left join  custom_order co on co.id=op.custom_order_id


        and   co.status != 8

            AND co.branch_id = #{branchId}


            and  DATE_FORMAT(co.created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')


            and  DATE_FORMAT(co.created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')


            AND co.order_product_type=#{type}


            AND op.series=#{series}

select a.*,b.* from  (select  count(co.id) payNum,IFNULL(SUM(co.factory_final_price) ,0) payMoney
  from  custom_order co


          and   co.status != 8

              AND co.branch_id = #{branchId}


              and  DATE_FORMAT(co.created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')


              and  DATE_FORMAT(co.created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')


              AND co.order_product_type=#{type}


  )a,
  (select  count(co.id) nopayNum,IFNULL(SUM(co.factory_final_price) ,0)nopayMoney
  from  custom_order co


          and ( co.status <2 or co.status =9 )
          and  DATE_SUB(CURDATE(), INTERVAL 30 DAY)<=co.created

              AND co.branch_id = #{branchId}


              AND co.order_product_type=#{type}


  )b

select  count(op.id) count,IFNULL(SUM(op.price) ,0)productPrice
from order_product op
left join  custom_order co on co.id=op.custom_order_id



        and   (co.status <2 or co.status=9) 

            AND co.branch_id = #{branchId}


            and  DATE_FORMAT(co.created,'%Y-%m-%d')>=DATE_FORMAT(#{startTime},'%Y-%m-%d')


            and  DATE_FORMAT(co.created,'%Y-%m-%d')<=DATE_FORMAT(#{endTime},'%Y-%m-%d')


            AND co.order_product_type=#{type}


            AND op.series=#{series}
