select,IFNULL(c.name,cc.name) dealerName,c.address as dealerAddress,o.estimated_delivery_date estimatedDeliveryDate ,IFNULL(uuu.name,uuuuu.name) customerName ,uuuu.name merchandiserName ,b.value payName,bb.value typeName ,bbb.value wayName,bbbb.value stateName ,bbbbb.value permitName from produce_order p left join user u on p.creator = u.id left join user uu on p.update_user = uu.id left join custom_order o on o.id = p.custom_order_id left join aftersale_apply a on a.id = p.custom_order_id left join custom_order oo on oo.id = a.custom_order_id left join company_customer uuuuu on oo.customer_id = uuuuu.id left join company cc on a.company_id = cc.id left join company c on o.company_id = c.id left join company_customer uuu on o.customer_id = uuu.id left join user uuuu on p.plan_creator = uuuu.id left join basecode b on b.code = p.is_pay left join basecode bb on bb.code = p.type left join basecode bbb on bbb.code = p.way left join basecode bbbb on bbbb.code = p.state left join basecode bbbbb on bbbbb.code = p.permit
and ( p.flag =0 or p.flag is null) and (b.type = 'produceOrderPay' ) and (bb.type = 'produceOrderType') and (bbb.type = 'produceOrderWay' ) and (bbbb.type = 'produceOrderState' ) and (bbbbb.type = 'produceOrderPermit')
AND p.no like '%${no}%'
AND p.custom_order_id = #{orderId}
AND p.custom_order_no like '%${orderNo}%'
AND p.state in
#{sta}
AND p.way in
#{w}
AND p.type in
#{t}
AND p.coordination_name=#{outFactoryName}
AND p.is_pay = #{pay}
AND DATE_FORMAT(p.actual_time,'%Y-%m-%d') >= #{payTime}
AND DATE_FORMAT(p.actual_time,'%Y-%m-%d') <= #{endPayTime}
AND p.planned_time is null
AND p.planned_time is not null
AND p.branch_id = #{branchId}
AND p.flag = #{flag}
AND to_days(p.actual_time) = to_days(now())
AND to_days(p.planned_time) = to_days(now())
AND TO_DAYS( NOW( ) ) - TO_DAYS( p.planned_time) >= 1
AND to_days(p.completion_time) = to_days(now())

